catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    if (newValue == 'lidia') {
        g_form.setVisible('attachments_lidia', true);
		g_form.setVisible('attachments_sculi', false);
		g_form.setVisible('attachments_dpf', false);
    }
	if (newValue == 'sculi') {
        g_form.setVisible('attachments_sculi', true);
		g_form.setVisible('attachments_lidia', false);
		g_form.setVisible('attachments_dpf', false);
    }
	if (newValue == 'dpf') {
        g_form.setVisible('attachments_dpf', true);
		g_form.setVisible('attachments_lidia', false);
		g_form.setVisible('attachments_sculi', false);
    }


}
