sys_script_include.script:
var Internal_account = Class.create();
Internal_account.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getInternalAccount: function() {

   //      gs.info("start");

		var account = this.getParameter('sysparm_account');
        var gr = new GlideRecord('customer_account');
		gr.get(account);

	//	 gs.info("value :" +gr.u_internalcustomer );

		if(gr.u_internal_customer == true){
			return true;
		}else if(gr.u_internal_customer == false){
			return false;
		}

    },
    type: 'Internal_account'
});
