catalog_script_client.script:
function onLoad() {

}

setProbableCause = function() {
	var failure_description = "";
	var failure_code = g_form.getValue("failure_code").trim();
	var failure_description_form = g_form.getValue("html_description").trim();
	var service_information = g_form.getValue("service_information").trim();
	var open_tag_pos;
	var closen_tag_pos;

	if (failure_code != "") {
		failure_description += failure_code;
	}
	if (failure_description_form != "") {
		open_tag_pos = failure_description_form.indexOf(">") + 1;
		closen_tag_pos = failure_description_form.lastIndexOf("<");

		if (failure_description != "") {
			failure_description += " - ";
		}
		failure_description += failure_description_form.substring(open_tag_pos, closen_tag_pos);
	}
	if (service_information != "") {
		failure_description += service_information;
	}

	g_form.setValue("probable_cause", failure_description);
};
