catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	if (newValue == 'Yes'){
		g_form.setVisible('new_special_tool_part_number_available', true);
		g_form.setMandatory('new_special_tool_part_number_available', true);
		g_form.setVisible('type_of_price', true);
		g_form.setMandatory('type_of_price', true);
		g_form.setVisible('bel', true);
		g_form.setVisible('neto', true);

		g_form.setMandatory('need', false);
		g_form.setMandatory('deadline', false);
		g_form.setMandatory('list_of_needed_parts', false);
		g_form.setMandatory('quantity', false);
		g_form.setMandatory('article_id', false);
		g_form.setMandatory('article_description', false);
		g_form.setMandatory('component_description', false);
		g_form.setMandatory('id', false);
		g_form.setMandatory('designation', false);
		g_form.setMandatory('purpose_of_request', false);
		g_form.setMandatory('forecast', false);
		g_form.setMandatory('maintanance_plan', false);
		g_form.setMandatory('sbom', false);
		g_form.setMandatory('lcc_result', false);
		//g_form.setMandatory('type_of_price', false);
		//g_form.setMandatory('bel', false);
		//g_form.setMandatory('neto', false);

		g_form.setVisible('company', false);
		g_form.setVisible('deadline', false);
		g_form.setVisible('list_of_needed_parts', false);
		g_form.setVisible('quantity', false);
		g_form.setVisible('article_id', false);
		g_form.setVisible('article_description', false);
		g_form.setVisible('component_description', false);
		g_form.setVisible('id', false);
		g_form.setVisible('designation', false);
		g_form.setVisible('purpose_of_request', false);
		g_form.setVisible('forecast', false);
		g_form.setVisible('maintanance_plan', false);
		g_form.setVisible('sbom', false);
		g_form.setVisible('lcc_result', false);
		//g_form.setVisible('type_of_price', false);
		//g_form.setVisible('bel', false);
		//g_form.setVisible('neto', false);
	}
	if (newValue == 'No'){
		g_form.setVisible('article_id', true);
		g_form.setMandatory('article_id', true);
		g_form.setMandatory('article_description', true);
		g_form.setVisible('article_description', true);
		g_form.setVisible('type_of_price', true);
		g_form.setMandatory('type_of_price', true);
		g_form.setVisible('bel', true);
		g_form.setVisible('neto', true);

		g_form.setMandatory('new_special_tool_part_number_available', false);
		g_form.setMandatory('need', false);
		g_form.setMandatory('deadline', false);
		g_form.setMandatory('list_of_needed_parts', false);
		g_form.setMandatory('quantity', false);
		g_form.setMandatory('component_description', false);
		g_form.setMandatory('id', false);
		g_form.setMandatory('designation', false);
		g_form.setMandatory('purpose_of_request', false);
		g_form.setMandatory('forecast', false);
		g_form.setMandatory('maintanance_plan', false);
		g_form.setMandatory('sbom', false);
		g_form.setMandatory('lcc_result', false);
		//g_form.setMandatory('type_of_price', false);
		//g_form.setMandatory('bel', false);
		//g_form.setMandatory('neto', false);


		g_form.setVisible('new_special_tool_part_number_available', false);
		g_form.setVisible('company', false);
		g_form.setVisible('deadline', false);
		g_form.setVisible('list_of_needed_parts', false);
		g_form.setVisible('quantity', false);
		g_form.setVisible('component_description', false);
		g_form.setVisible('id', false);
		g_form.setVisible('designation', false);
		g_form.setVisible('purpose_of_request', false);
		g_form.setVisible('forecast', false);
		g_form.setVisible('maintanance_plan', false);
		g_form.setVisible('sbom', false);
		g_form.setVisible('lcc_result', false);
		//g_form.setVisible('type_of_price', false);
		//g_form.setVisible('bel', false);
		//g_form.setVisible('neto', false);
	}


}
