catalog_script_client.script:
function onLoad () {
	/* exported onLoad */	
	/* global setTimeout */

	var $ = this.jQuery,		
	fieldName = "sub_application"; // !!! it's the name of any variable from variable set !!!


	setTimeout(function () {
		var $container = $(".form-container-caption").closest(".sp-form-container");

		$container.prepend("<button style='position:absolute;right:500px;margin-right:10px;margin-bottom:2px;font-size:45%;' class='btn btn-icon my-collapse-button'>" +
		"<i class='fa fa-plus fa-lg'></i></button>");
		$container.children("div.row").hide();
		$(".my-collapse-button").click(function () {
			var $button = $(this);
			$button.parent().children("div.row")
				.toggle();
			$button.find("i")
				.toggleClass("fa-plus fa-minus");
		});
	}, 0);


	//el.style.margin = '0px 0px 0px -100px';
}
