catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue === '') 
		return;

	var cur_account = g_form.getValue('account_mrvs');
	var cur_soldproduct = g_form.getValue('product_mrvs');
	var cur_installbaseitem = g_form.getValue('serial_number_mrvs');

	var gaSc = new GlideAjax('Support_case');
	gaSc.addParam('sysparm_name', 'getOperatingHours');
	gaSc.addParam('sysparm_installbase', cur_installbaseitem);
	gaSc.getXML(showMessage);

	function showMessage(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue('current_operating_hours_mrvs', answer);
		g_form.setReadOnly('current_operating_hours_mrvs',true);
	}

	//g_form.addInfoMessage('acc ' + cur_account + 'Prod '+cur_soldproduct + 'sn '+cur_installbaseitem);

	g_form.setValue('u_serial_number',g_form.getValue('serial_number_mrvs'),g_form.getDisplayValue('serial_number_mrvs'));

	var ga = new GlideAjax('sn_contributor.ContributorUserPortalAJAX');
	ga.addParam('sysparm_name', 'getInstallBaseFieldsForAccount');
	ga.addParam('sysparm_account', cur_account);
	ga.addParam('sysparm_installbaseitem', cur_installbaseitem);
	ga.getXML(function(serverResponse) {
		var fields = serverResponse.responseXML.getElementsByTagName('fields');
		setFieldValues('product_mrvs', fields, cur_soldproduct, 'sold_arr');
	});
	//g_form.addInfoMessage(fields);

}

function setFieldValues(field, result, current, array) {
	for (var i = 0; i < result.length; i++) {
		var arr = result[i].getAttribute(array);
		if (arr.length > 0 && arr.split(',').length == 1) {
			g_form.setValue(field, arr);
		}
	}
}
