catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}
	if (newValue == 'omm'){
		g_form.setVisible('english', true);
		g_form.setVisible('german', true);
		g_form.setVisible('french', true);
		g_form.setVisible('italian', true);
		g_form.setVisible('spanish', true);
		g_form.setVisible('russian', true);
	}
	else if (newValue == 'ai' || newValue == 'rsm'){
		g_form.setVisible('english', true);
		g_form.setVisible('german', true);
		g_form.setVisible('french', true);
		g_form.setVisible('italian', false);
		g_form.setVisible('spanish', false);
		g_form.setVisible('russian', false);
	}

}
