sp_widget.script:
(function(){
	var gr = $sp.getInstanceRecord();
	data.href = $sp.getMenuHREF(gr);
	data.displaylink = 1;

	//LH Specific: If link is organizational information" then dynamically build the URL.
	if(data.href == "/csm?id=kb_search&kb_knowledge_base=##kb##&spa=1&language=en&kb_category=##kbcat##")
		{
			 // Get the current user record
			var currentuser = gs.getUser();
			console.log("currentuser:"+currentuser.getDisplayName());

			// Get the current company record
			var company = new GlideRecord('core_company');
			company.addQuery("sys_id", currentuser.getCompanyID());
			company.query();
			company.next(); //get the first record
			console.log("companyName:"+company.getDisplayValue('name'));

			// Get the current account record (CSM) equal to the user company
			var account = new GlideRecord('customer_account');
			account.addQuery("name",company.getDisplayValue('name'));
			account.query();
			account.next();
			console.log("accountName:"+account.getDisplayValue('name'));
			console.log("kb:"+account.getDisplayValue('u_knowledgebase'));
			console.log("kbid:"+account.getValue('u_knowledgebase'));

			// Get the KB Category ID for organizational_information record
			var kb_category = new GlideRecord('kb_category');
			kb_category.addQuery("value","organizational_information");
			kb_category.query();
			kb_category.next();
			console.log("kb_category:"+kb_category.getDisplayValue('value'));
			console.log("kb_categoryid:"+kb_category.getValue('sys_id'));

			//Construct the URL  if params not null
			if(account.getDisplayValue('u_knowledgebase') != "" && kb_category.getDisplayValue('value') !=""){
				data.href = data.href
					.replace("##kb##",account.getValue('u_knowledgebase'))
					.replace("##kbcat##",kb_category.getValue('sys_id'));
			}
			else
				data.displaylink = 0; // hide icon if param null
	}

	data.target = options.target || "";
	var img = new GlideRecord('db_image');
	img.addQuery('sys_id', options.image);
	img.query()
	if (img.next()){
		data.image = img.getValue('name')
	}
})();
