sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
  /* cockrl0 new.start */
	c.server.update();
	$rootScope.$on('updateKB', function(event, data) {
		if(data) {
			c.data.zoomKB = true;
			c.data.KnowledgeBase = data;
			c.server.update();
		} else {
			c.data.zoomKB = false;
			c.data.KnowledgeBase = null;
			c.server.update();
		}
	});
  /* cockrl0 new.end */
};

