sp_widget.script:
(function(){
	var gr = $sp.getInstanceRecord();
	data.href = $sp.getMenuHREF(gr);
	/* data.displaylink = 1; cockrl0 old */
	data.displaylink = false; /* cockrl0 new */

	//LH Specific: If link is organizational information" then dynamically build the URL.
	//if(data.href == "/csm?id=csm_kb_search&kb_knowledge_base=##kb##&spa=1&language=en&kb_category=##kbcat##")
	if(data.href.includes("##"))
		{
			// Get the current user record
			var currentuser = gs.getUser();

			// Get the current company record
			var company = new GlideRecord('core_company');
			company.addQuery("sys_id", currentuser.getCompanyID());
			company.query();
			company.next(); //get the first record
//			console.log("companyName:"+company.getDisplayValue('name'));

			// Get the current account record (CSM) equal to the user company
			var account = new GlideRecord('customer_account');
			account.addQuery("name",company.getDisplayValue('name'));
			account.query();
			account.next();
//			console.log("accountName:"+account.getDisplayValue('name'));
//			console.log("kb:"+account.getDisplayValue('u_customer_knowledge_base'));
//			console.log("kbid:"+account.getValue('u_customer_knowledge_base'));

			/* cockrl0 new.start */
			var knowledgeTitle;
			var knowledgeId;
			if (input.zoomKB) {
				knowledgeTitle = input.KnowledgeBase.title;
				knowledgeId = input.KnowledgeBase.sys_id;
			} else {
				knowledgeTitle = account.getDisplayValue('u_customer_knowledge_base');
				knowledgeId = account.getValue('u_customer_knowledge_base');
			}
			/* cockrl0 new.end */

			// Get the 1st Level Category ID
			var kb_category_1st = new GlideRecord('kb_category');
			/* kb_category_1st.addQuery("parent_id",account.getValue('u_customer_knowledge_base')); cockrl0 old */
			kb_category_1st.addQuery("parent_id",knowledgeId); /* cockrl0 new */
			kb_category_1st.addQuery("value",options.kb_1st_category_level);	
			kb_category_1st.query();
			kb_category_1st.next();
//			console.log("kb_category:"+kb_category_1st.getDisplayValue('value'));
//			console.log("kb_categoryid:"+kb_category_1st.getValue('sys_id'));

			// Get the 2nd Level Category ID
			var kb_category_2nd = new GlideRecord('kb_category');
			kb_category_2nd.addQuery("parent_id",kb_category_1st.sys_id);
			kb_category_2nd.addQuery("value",options.kb_2nd_category_level);			
			kb_category_2nd.query();
			kb_category_2nd.next();

			// Get the 3th Level Category ID
			var kb_category_3th = new GlideRecord('kb_category');
			kb_category_3th.addQuery("parent_id",kb_category_2nd.sys_id);
			kb_category_3th.addQuery("value",options.kb_3th_category_level);
			kb_category_3th.query();
			kb_category_3th.next();
//			console.log("kb_category:"+kb_category.getDisplayValue('value'));
//			console.log("kb_categoryid:"+kb_category.getValue('sys_id'));

			// Get the 4th Level Category ID
			var kb_category_4th = new GlideRecord('kb_category');
			kb_category_4th.addQuery("parent_id",kb_category_2nd.sys_id);
			kb_category_4th.addQuery("value",options.kb_4th_category_level);
			kb_category_4th.query();
			kb_category_4th.next();
//			console.log("kb_category:"+kb_category.getDisplayValue('value'));
//			console.log("kb_categoryid:"+kb_category.getValue('sys_id'));
			var kb_category;
			if (options.kb_4th_category_level != "") {
				kb_category = kb_category_4th.getValue('sys_id');
			}
			else {
				kb_category = kb_category_3th.getValue('sys_id');
			}

			//Construct the URL  if params not null
			/* if(account.getDisplayValue('u_customer_knowledge_base') != "" && kb_category !=""){ cockrl0 old */
			if(knowledgeTitle && kb_category) { /* cockrl0 new */
				data.href = data.href
					/* .replace("##kb##",account.getValue('u_customer_knowledge_base')) cockrl0 old */
					.replace("##kb##",knowledgeId) /* cockrl0 new */
					.replace("##kbcat##",kb_category);
				data.displaylink = true; /* cockrl0 new */
			}
			/* cockrl0 old.start
			else {
				data.displaylink = 0; // hide icon if param null
			}
			cockrl0 old.end */
	}

	data.target = options.target || "";
	var img = new GlideRecord('db_image');
	img.addQuery('sys_id', options.image);
	img.query();
	if (img.next()){
		data.image = img.getValue('name');
	}
})();
