sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
	// COT BU-VM : INC0099076 | new.start
	var notification = new ArticlesNotifications();
	var activityType = current.version ? 1 : 0;
	notification.createNotificationServer(current.author, current.sys_id, current.kb_knowledge_base, activityType);
	// COT BU-VM : INC0099076 | new.end

	if(current.wiki) {
		var id_display = current.display_number ? current.display_number : current.number + " v1.0";
		setTag(current.wiki.split(","), id_display, current.sys_id);
		current.wiki = "";
	}
})(current, previous);

function setTag(tagList, id_display, table_key) {
	var i = 0;
	var gr;

	for(i; i < tagList.length; i++) {
		gr = new GlideRecord("label_entry");
		gr.initialize();

		gr.id_display = id_display;
		gr.id_type = "Knowledge";
		gr.label = tagList[i];
		gr.notify_onchange = "false";
		gr.read = "yes";
		gr.table = "kb_knowledge";
		gr.table_key = table_key;
		gr.title = "Knowledge - " + id_display;
		gr.url = "kb_knowledge.do?sys_id=" + table_key + "&sysparm_view=";

		gr.insert();
	}
}
