sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var offeringGroup = new GlideAjax('x_liisa_support_ca.Support_case');
    offeringGroup.addParam('sysparm_name', 'getServiceOfferingGroup');
    offeringGroup.addParam('sysparm_offering', newValue);
    offeringGroup.getXML(getBack);

    function getBack(response) {

        var suportGroup = response.responseXML.documentElement.getAttribute("answer");
        if (suportGroup != null) {
            g_form.setValue('assignment_group', suportGroup);

        }
    }

    //Type appropriate comment here, and begin script below

}
