sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var baseURL = gs.getProperty('glide.servlet.uri');
	var link = "";
	if(current.getValue('contact')){
		link = baseURL + 'csm?id=csm_ticket&table=x_liisa_support_ca_services&sys_id='+current.getUniqueValue()+'&view=csp';			
	}
	else if(current.getValue('consumer')){	
		link = baseURL + 'csp?id=csm_ticket&table=x_liisa_support_ca_services&sys_id='+current.getUniqueValue()+'&view=css';	
	}

	var ep = new global.CSManagementUtils().getExtensionPoints("global.CSMURLProcessor");
	for (var i = 0; i < ep.length; i++) {
		var point = ep[i];
		link = point.updateURL(link, current);
	}
	template.print(link);
})(current, template, email, email_action, event);
