sys_script_include.script:
var GetLastAttachmentSysIdByUser = Class.create();
GetLastAttachmentSysIdByUser.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	isPublic: function() {
		return true;
	},

	getAttachmentSysId: function() {
		var result = {};
		var attachment_record = "";
		var sys_id = "";
		var user = "";

		user = this.getParameter("user");

		attachment_record = this._getLastAttachmentRecord(user);
		sys_id = attachment_record.getValue("sys_id");

		result = {
			"sys_id" : sys_id
		};

		return JSON.stringify(result);
	},

	_getLastAttachmentRecord: function(user) {
		var result = "";

		var attachment_table = new GlideRecord("sys_attachment");
		attachment_table.addQuery("sys_created_by", user);
		attachment_table.orderByDesc("sys_created_on");
		attachment_table.setLimit(1);
		attachment_table.query();

		while(attachment_table.next()) {
			result = attachment_table;
		}

		return result;
	},

    type: 'GetLastAttachmentSysIdByUser'
});
