sys_script_include.script:
var UpdateSerialNumber = Class.create();
UpdateSerialNumber.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    isPublic: function() {
		return true;
	},

	updateSerialNumber: function(){
		var grSN = new GlideRecord("alm_hardware");
		grSN.addQuery("serial_number", this.getParameter("serial_number"));
		grSN.query();

		while(grSN.next()) {
			grSN.setValue("u_commissioning_reason", this.getParameter("commissioning_reason"));
			grSN.setValue("u_commissioning_date",  new GlideDateTime(this.getParameter("commissioning_date")));
			grSN.setValue("u_commissioning_location", this.getParameter("commissioning_location"));
			grSN.setValue("u_equipment_serial_number", this.getParameter("equipment_sn"));
			grSN.setValue("u_equipment_operating_hours", this.getParameter("equipment_operating_hours"));
			grSN.setValue("u_name_of_person_submitting_commissioning_report", this.getParameter("name_of_the_person_submitting_the_form"));
			grSN.setValue("u_name_of_person_acknowledging_commissioning_report", this.getParameter("name_of_the_person_acknowledging_the_form"));
			grSN.update();
		}

		return 0;
	},

    type: 'UpdateSerialNumber'
});
