sys_script_include.script:
var Parent_Account = Class.create();
Parent_Account.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	isPublic: function() {
		return true;
	},

	getParentAccount: function() {
		var result = {};
		result.parentcompanyId = '';
		result.parentcompanyName = '';

		var account = this.getParameter('sysparm_account');
		var gr = new GlideRecord('customer_account');

		if (gr.get(account)) {
			result.parentcompanyId = gr.getValue('account_parent');
			result.parentcompanyName = gr.account_parent.getDisplayValue();
		}

		return new global.JSON().encode(result);
	},	
    type: 'Parent_Account'
});
