sys_script_include.script:
var User_Company = Class.create();
User_Company.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getUserCompany: function() {
		var result = {};
		result.companyId = '';
		result.companyName = '';
		result.accountcompanyId = '';

		var user = this.getParameter('sysparm_user');
		var gr = new GlideRecord('sys_user');
		if (gr.get(user)) {
			result.companyId = gr.getValue('company');
			result.companyName = gr.company.getDisplayValue();
		}

	var gr1 = new GlideRecord('customer_account');
	if (gr1.get(result.companyName)) {
			result.accountcompanyId = gr1.getValue('sys_id');
		}

		return new global.JSON().encode(result);
		//return JSON.stringify(result);
		//return result.accountcompanyId;
		},
		type: 'User_Company'
});
