sys_script_include.script:
var SupportCaseNestedGroupsAssignment = Class.create();
SupportCaseNestedGroupsAssignment.prototype = {
	initialize: function() {
	},
	returnParentGroupUsers: function() {
		var arrUsers = [];
		var arrGroups = [];
		arrGroups.push(current.assignment_group.toString());

		var grName = new GlideRecord('sys_user_group');
		grName.addEncodedQuery('parent=' + current.assignment_group);
		grName.query();

		while(grName.next()) {
			arrGroups.push(grName.sys_id.toString());
			arrGroups= this.childGroups(grName.sys_id, arrGroups);

		}
		var grUser = new GlideRecord('sys_user_grmember');
		grUser.addEncodedQuery('groupIN'+ arrGroups);
		grUser.query();

		while (grUser.next()) {

			arrUsers.push(grUser.getValue('user'));
		}

		return 'sys_idIN' + arrUsers;
	},
	childGroups: function(parentID, arrGroups){
		var grChild = new GlideRecord('sys_user_group');
		grChild.addQuery('parent', parentID);
		grChild.addQuery('active', 'true');
		grChild.query();
		while(grChild.next()){
			arrGroups.push(grChild.sys_id.toString());
			this.childGroups(grChild.sys_id, arrGroups);
		}
		return arrGroups;
	},
	type: 'SupportCaseNestedGroupsAssignment'
};
